mapper.select:
select * from course_schedule
    where teacher_id = #{teacher_id}
      and school_year = #{school_year}
      and semester = #{semester}

select * from course_schedule where teacher_id = #{teacher_id}
                                and class_id = #{class_id}
                                and course_id = #{course_id}

select * from course_schedule where teacher_id = #{teacher_id}
                                and weekday = #{weekday}
                                and session = #{session}
                                and school_year = #{school_year}
                                and semester = #{semester}

select * from course_schedule where class_id = #{class_id}
                                and weekday = #{weekday}
                                and session = #{session}

select * from course_schedule where room_id = #{room_id}
                                and weekday = #{weekday}
                                and session = #{session}
                                and school_year = #{semester}

mapper.update:
update course_sechedule set section = #{section}
    where teacher_id = #{teacher_id}
      and class_id = #{class_id}
      and course_id = #{course_id}

update course_sechedule set room_id = #{room_id}
    where teacher_id = #{teacher_id}
      and class_id = #{class_id}
      and course_id = #{course_id}

update course_sechedule set weekday = #{weekday}
    where teacher_id = #{teacher_id}
      and class_id = #{class_id}
      and course_id = #{course_id}
